para.example:
   Operaciones sobre bits y enteros


<?php
/*
 * Ignórese esta parte,
 * es solo formato para clarificar los resultados
 */

$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
        . ' %3$s (%4$2d = %4$04b)' . "\n";

echo <<<EOH
 ---------     ---------  -- ---------
 resultado       valor        prueba
 ---------     ---------  -- ---------
EOH;


/*
 * Aquí están los ejemplos
 */

$values = array(0, 1, 2, 4, 8);
$test = 1 + 4;

echo "\n AND a nivel de bits \n";
foreach ($values as $value) {
    $result = $value & $test;
    printf($format, $result, $value, '&', $test);
}

echo "\n OR inclusivo a nivel de bits \n";
foreach ($values as $value) {
    $result = $value | $test;
    printf($format, $result, $value, '|', $test);
}

echo "\n OR exclusivo a nivel de bits (XOR) \n";
foreach ($values as $value) {
    $result = $value ^ $test;
    printf($format, $result, $value, '^', $test);
}
?>


   &example.outputs;


---------     ---------  -- ---------
 resultado       valor        prueba
 ---------     ---------  -- ---------
 AND a nivel de bits
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 OR inclusivo a nivel de bits
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 OR exclusivo a nivel de bits (XOR)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

Desplazamiento de bits sobre enteros
<?php /* * Aquí están algunos ejemplos */
echo "\n--- Desplazamientos a la derecha sobre enteros positivos ---\n";
$val = 4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'copia del bit de signo ahora a la izquierda');
$val = 4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places);
$val = 4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'bits salieron por la derecha');
$val = 4; $places = 4; $res = $val >> $places; p($res, $val, '>>', $places, 'mismo resultado que arriba: no hay desplazamiento más allá de 0');
echo "\n--- Desplazamientos a la derecha sobre enteros negativos ---\n";
$val = -4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'copia del bit de signo ahora a la izquierda');
$val = -4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places, 'bits salieron por la derecha');
$val = -4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'mismo resultado que arriba: no hay desplazamiento más allá de -1');
echo "\n--- Desplazamientos a la izquierda sobre enteros positivos ---\n";
$val = 4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'complemento de ceros a la derecha');
$val = 4; $places = (PHP_INT_SIZE * 8) - 4; $res = $val << $places; p($res, $val, '<<', $places);
$val = 4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places, 'el bit de signo salió');
$val = 4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits salieron a la izquierda');
echo "\n--- Desplazamientos a la izquierda sobre enteros negativos ---\n";
$val = -4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'complemento de ceros a la derecha');
$val = -4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places);
$val = -4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits salieron a la izquierda, incluyendo el bit de signo');
/* * Ignórese esta sección * Contiene código para el formato de este ejemplo */
function p($res, $val, $op, $places, $note = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("Expresión : %d = %d %s %d\n", $res, $val, $op, $places);
echo " Decimal :\n"; printf(" val=%d\n", $val); printf(" res=%d\n", $res);
echo " Binario :\n"; printf(' val=' . $format, $val); printf(' res=' . $format, $res);
if ($note) { echo " Nota : $note\n"; }
echo "\n\n"; } ?>
