  &reftitle.examples;



    Ejemplo de AMQPQueue::consume


<?php

/* Crea una conección utilizando todas als credenciales por defecto: */
$connection = new AMQPConnection();
$connection->connect();

/* Crea un objeto de la cola */
$queue = new AMQPQueue($connection);

// Declara la cola
$queue->declare('myqueue');

$options = array(
 'min' => 1,
 'max' => 10,
 'ack' => true
);

// Toma los mensajes
$messages = $queue->consume($options);

foreach ($messages as $message) {
 echo $message['message_body'];
}

?>
