  &reftitle.examples;



    Ejemplo con bzread


<?php

$file = "/tmp/foo.bz2";
$bz = bzopen($file, "r") or die("Imposible abrir el archivo $file");

$decompressed_file = '';
while (!feof($bz)) {
  $decompressed_file .= bzread($bz, 4096);
}
bzclose($bz);

echo "El contenido del archivo $file es : <br />\n";
echo $decompressed_file;

?>
