  &reftitle.examples;



    ejemplo de bzread


<?php

$file = "/tmp/foo.bz2";
$bz = bzopen($file, "r") or die("No se ha podido abrir $file");

$decompressed_file = '';
while (!feof($bz)) {
  $decompressed_file .= bzread($bz, 4096);
}
bzclose($bz);

echo "El contenido de $file es: <br />\n";
echo $decompressed_file;

?>
