refentry.refsect1:
&reftitle.description;

 boolclasskit_method_redefine
 stringclassname
 stringmethodname
 stringargs
 stringcode
 intflagsCLASSKIT_ACC_PUBLIC

&note.classkit.selfmanipulation;
&warn.experimental.func;

  &reftitle.examples;


   
    Ejemplo de classkit_method_redefine
    

<?php
class Ejemplo {
    function foo() {
        return "foo!\n";
    }
}

// crear un objeto de Ejemplo
$e = new Ejemplo();

// imprimir Ejemplo::foo() (antes de redefinir)
echo "Antes: " . $e->foo();

// Redefinir el método 'foo'
classkit_method_redefine(
    'Ejemplo',
    'foo',
    '',
    'return "bar!\n";',
    CLASSKIT_ACC_PUBLIC
);

// imprimir Ejemplo::foo() (después de redefinir)
echo "Después: " . $e->foo();
?>

    
    &example.outputs;
    

Antes: foo!
Después: bar!
