  &reftitle.parameters;


   
    
     filename
     


       El nombre de archivo que se va a comprobado.

  &reftitle.examples;


   
    Ejemplo de is_uploaded_file
    

<?php

if (is_uploaded_file($_FILES['archivo_de_usuario']['nombre_tmp'])) {
   echo "Archivo ". $_FILES['archivo_de_usuario']['nombre'] ." subido con éxtio.\n";
   echo "Monstrar contenido\n";
   readfile($_FILES['archivo_de_usuario']['nombre_tmp']);
} else {
   echo "Posible ataque del archivo subido: ";
   echo "nombre del archivo '". $_FILES['archivo_de_usuario']['nombre_tmp'] . "'.";
}

?>
