refentry.refsect1:
  &reftitle.examples;



    Ejemplo de ftp_mdtm


<?php

$archivo = 'algun_archivo.txt';

// configurar la conexion basica
$id_con = ftp_connect($servidor_ftp);

// iniciar sesion con nombre de usuario y contrasenya
$resultado_login = ftp_login($id_con, $ftp_nombre_usuario, $ftp_contrasenya);

// obtener la ultima fecha/hora de modificacion
$buf = ftp_mdtm($id_con, $archivo);

if ($buf != -1) {
    // algun_archivo.txt fue modificado por ultima vez en: March 26 2003 14:16:41.
    echo "$archivo fue modificado por &uacute;ltima vez en: " . date("F d Y H:i:s.", $buf);
} else {
    echo "No se pudo obtener mdtime";
}

// cerrar la conexion
ftp_close($id_con);

?>
