refentry.refsect1:
&reftitle.description;
boolftp_rename resourceftp_stream stringoldname stringnewname
ftp_rename renombra un archivo o un directorio en el servidor FTP.

  &reftitle.examples;



    Ejemplo de ftp_rename


<?php
$old_file = 'somefile.txt.bak';
$new_file = 'somefile.txt';

// establecer una conexión básica
$conn_id = ftp_connect($ftp_server);

// iniciar sesión con nombre de usuario y contraseña
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

// intentar renombrar el archivo $old_file a $new_file
if (ftp_rename($conn_id, $old_file, $new_file)) {
 echo "se ha renombrado $old_file a $new_file con éxito\n";
} else {
 echo "Hubo un problema al renombrar $old_file a $new_file\n";
}

// cerrar la conexión ftp
ftp_close($conn_id);
?>
