    Ejemplo de id3_get_version


<?php
$version = id3_get_version( "ruta/a/ejemplo.mp3" );
if ($version & ID3_V1_0) {
    echo "Contiene una etiqueta 1.x\n";
}
if ($version & ID3_V1_1) {
    echo "Contiene una etiqueta 1.1\n";
}
if ($version & ID3_V2) {
    echo "Contiene una etiqueta 2.x\n";
}
?>


