refentry.refsect1:
&reftitle.parameters;



  &gd.image.description;

   x



     Coordenada x del punto.




   y



     Coordenada y del punto.

  &reftitle.examples;



    Acceder a los distintos valores RGB


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);
$r = ($rgb >> 16) & 0xFF;
$g = ($rgb >> 8) & 0xFF;
$b = $rgb & 0xFF;

var_dump($r, $g, $b);
?>


    &example.outputs.similar;


int(119)
int(123)
int(180)




    Valores RGB legibles para humanos usando imagecolorsforindex


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);

$colores = imagecolorsforindex($im, $rgb);

var_dump($colores);
?>


    &example.outputs.similar;


array(4) {
  ["red"]=>
  int(119)
  ["green"]=>
  int(123)
  ["blue"]=>
  int(180)
  ["alpha"]=>
  int(127)
}
