refentry.refsect1:
  &reftitle.description;
  
   resourceimagecreatefromwbmp
   stringfilename
  


   imagecreatefromwbmp devuelve un identificador de imagen
   que representa la imagen obtenida desde el nombre de archivo dado.
  
  &tip.fopen-wrapper;

  &reftitle.parameters;


   
    
     filename
     


       Ruta de la imagen WBMP.

  &reftitle.examples;
  
   Ejemplo de manejar un error durante la carga de un WBMP
   

<?php
function CargarWBMP($imagen)
{
    /* Intentar abrir */
    $im = @imagecreatefromwbmp($imagen);

    /* Ver si falló */
    if(!$im)
    {
        /* Crear una imagen en blanco */
        $im  = imagecreatetruecolor(150, 30);
        $fondo = imagecolorallocate($im, 255, 255, 255);
        $ct  = imagecolorallocate($im, 0, 0, 0);

        imagefilledrectangle($im, 0, 0, 150, 30, $fondo);

        /* Imprimir un mensaje de error */
        imagestring($im, 1, 5, 5, 'Error cargando ' . $imagen, $ct);
    }

    return $im;
}

header('Content-type: image/vnd.wap.wbmp');

$img = CargarWBMP('falso.image');

imagewbmp($img);
imagedestroy($img);
?>
