&reftitle.parameters;




   result



     El identificador de resultados de la consulta

&reftitle.returnvalues;


 Devuelve valor de tipo integer si existe un valor de retorno, si no,
 devolverá &null;.

  &reftitle.examples;



    Obtener el valor de retorno de una llamada a un procedimiento


<?php

$enlace = ingres_connect($base_datos);

if ( ingres_errno() != 0 ) {
    $texto_error = ingres_error();
    die($texto_error);
}

$resultado = ingres_query($enlace, "execute procedure php_proc (value = 1000)");

if ( ingres_errno() != 0 ) {
    $texto_error = ingres_error();
    die($texto_error);
}

echo "valor de retorno - " . ingres_fetch_proc_return($resultado) . "\n";

ingres_close($enlace);

?>
