refentry.refsect1:
  &reftitle.examples;


    Ejemplo de normalizer_is_normalized



<?php
$char_A_ring = "\xC3\x85"; // 'LETRA LATINA MAYÚSCULA CON UN ARO ENCIMA' (U+00C5)
$char_combining_ring_above = "\xCC\x8A";  // 'COMBINANDO EL ARO DE ENCIMA' (U+030A)

$char_orig = 'A' . $char_combining_ring_above;
$char_norm = normalizer_normalize( 'A' . $char_combining_ring_above, Normalizer::FORM_C );

echo ( normalizer_is_normalized($char_orig, Normalizer::FORM_C) ) ? "normalizado" : "no normalizado";
echo '; ';
echo ( normalizer_is_normalized($char_norm, Normalizer::FORM_C) ) ? "normalizado" : "no normalizado";
?>




   Ejemplo orientado a objetos


<?php
$char_A_ring = "\xC3\x85"; // 'LETRA LATINA MAYÚSCULA CON UN ARO ENCIMA' (U+00C5)
$char_combining_ring_above = "\xCC\x8A";  // 'COMBINANDO EL ARO DE ENCIMA' (U+030A)

$char_orig = 'A' . $char_combining_ring_above;
$char_norm = Normalizer::normalize( 'A' . $char_combining_ring_above, Normalizer::FORM_C );

