  &reftitle.examples;


    Ejemplo de normalizer_normalize



<?php
$char_A_ring = "\xC3\x85"; // 'LETRA LATINA MAYÚSCULA CON UN ARO ENCIMA' (U+00C5)
$char_combining_ring_above = "\xCC\x8A";  // 'COMBINANDO EL ARO DE ENCIMA' (U+030A)

$char_1 = normalizer_normalize( $char_A_ring, Normalizer::FORM_C );
$char_2 = normalizer_normalize( 'A' . $char_combining_ring_above, Normalizer::FORM_C );

echo urlencode($char_1);
echo ' ';
echo urlencode($char_2);
?>




   Ejemplo Orientado a Objetos


<?php
