section.example:
   Ejemplo retrollamada de resultados


<?php
$m = new Memcached();
$m->addServer('localhost', 11211);
$items = array(
    'clave1' => 'valor1',
    'clave2' => 'valor2',
    'clave3' => 'valor3'
);
$m->setMulti($items);
$m->getDelayed(array('clave1', 'clave3'), true, 'rll_resultados');

function rll_resultados($memc, $item)
{
    var_dump($item);
}
?>


   &example.outputs.similar;


