    Ejemplo de Memcached::getMulti


<?php
$m = new Memcached();
$m->addServer('localhost', 11211);

$ítems = array(
    'clave1' => 'valor1',
    'clave2' => 'valor2',
    'clave3' => 'valor3'
);
$m->setMulti($ítems);
$resultado = $m->getMulti(array('clave1', 'clave3', 'clave_errónea'), $cas);
var_dump($resultado, $cas);
?>


