    Ejemplo de Memcached::GET_PRESERVE_ORDER


<?php
$m = new Memcached();
$m->addServer('localhost', 11211);

$data = array(
    'foo' => 'foo-data',
    'bar' => 'bar-data',
    'baz' => 'baz-data',
    'lol' => 'lol-data',
    'kek' => 'kek-data',
);

$m->setMulti($data, 3600);

$null = null;
$claves = array_keys($data);
$claves[] = 'zoo';
$obtenidas = $m->getMulti($claves, $null, Memcached::GET_PRESERVE_ORDER);

foreach ($obtenidas as $c => $v) {
    echo "$c $v\n";
}
?>


    &example.outputs.similar;


foo foo-data
bar bar-data
baz baz-data
lol lol-data
kek kek-data
zoo
