refentry.refsect1:
  &reftitle.description;

&style.oop;

   objectmysqli_result::fetch_field



&style.procedural;

   objectmysqli_fetch_field
   mysqli_resultresult



   Retorna la definición de una columna del resultset como un objeto. Para
   obtener la información de todas las columnas del resultset hay que llamar esta
   función reiterativamente.

  &reftitle.examples;


&style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* verificar la conexión */
if (mysqli_connect_errno()) {
    printf("Conexión fallida: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Obtener la información del campo de cada columna */
    while ($finfo = $result->fetch_field()) {

        printf("Nombre:     %s\n", $finfo->name);
        printf("Tabla:    %s\n", $finfo->table);
        printf("Largo max: %d\n", $finfo->max_length);
        printf("Banderas:    %d\n", $finfo->flags);
        printf("Tipo:     %d\n\n", $finfo->type);
    }
    $result->close();
}

/* cerrar la conexión */
$mysqli->close();
?>



&style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* verificar la conexión *
if (mysqli_connect_errno()) {
    printf("Conexión fallida: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Obtener la información del campo de cada columna */
    while ($finfo = mysqli_fetch_field($result)) {

        printf("Nombre:     %s\n", $finfo->name);
        printf("Tabla:      %s\n", $finfo->table);
        printf("Largo max:  %d\n", $finfo->max_length);
        printf("Banderas:   %d\n", $finfo->flags);
        printf("Tipo:       %d\n\n", $finfo->type);
    }
    mysqli_free_result($result);
}

/* cerrar la conexión */
mysqli_close($link);
?>


   &examples.outputs;


Nombre:     Name
Tabla:      Country
Largo max:  11
Banderas:   1
Tipo:       254

Nombre:     SurfaceArea
Tabla:      Country
Largo max:  10
Banderas:   32769
