is_select(                    CREATE TABLE test(id INT)): no almacenada
is_select(    INSERT INTO test(id) VALUES (1), (2), (3)): no almacenada
is_select(             SELECT id FROM test WHERE id = 1): almacenada
is_select(             SELECT id FROM test WHERE id = 1): almacenada
is_select(                           SELECT * FROM test): almacenada
