    Un ejemplo de Phar::hasMetadata


<?php
try {
    $phar = new Phar('miphar.phar');
    var_dump($phar->hasMetadata());
    $phar->setMetadata(array('cosa' => 'hola'));
    var_dump($phar->hasMetadata());
    $phar->delMetadata();
    var_dump($phar->hasMetadata());
} catch (Exception $e) {
    // manejar errores
}
?>


