  Ejemplo Quickhash


<?php
$set = new QuickHashIntSet( 1024, QuickHashIntSet::CHECK_FOR_DUPES );
$set->add( 1 );
$set->add( 3 );

var_dump( $set->exists( 3 ) );
var_dump( $set->exists( 4 ) );

$set->saveToFile( "/tmp/test-set.set" );

$newSet = QuickHashIntSet::loadFromFile(
    "/tmp/test-set.set"
);

var_dump( $newSet->exists( 3 ) );
var_dump( $newSet->exists( 4 ) );
?>


  &example.outputs.similar;


bool(true)
bool(false)
bool(true)
bool(false)




  Ejemplo Quickhash ArrayAccess


<?php
$hash = new QuickHashIntHash( 64 );

// Agregar y actualizar las entradas de hash.
$hash[3] = 145926;
$hash[3] = 1415926;
$hash[2] = 72;

// Comprobar si existen claves
var_dump( isset( $hash[3] ) );

// Eliminación de entradas de hash
unset( $hash[2] );

// Recuperar el valor almacenado para un hash
echo $hash[3], "\n";
?>


  &example.outputs.similar;


bool(true)
1415926




  Ejemplo Quickhash Iterator


<?php
$hash = new QuickHashIntHash( 64 );

// Añadiendo entradas hash
$hash[1] = 145926;
$hash[2] = 1415926;
$hash[3] = 72;
$hash[4] = 712314;
$hash[5] = -4234;

foreach( $hash as $key => $value )
{
    echo $key, ' => ', $value, "\n";
}
?>


  &example.outputs.similar;


5 => -4234
4 => 712314
1 => 145926
2 => 1415926
3 => 72




  Ejemplo Quickhash valores String


<?php
$hash = new QuickHashIntStringHash( 64 );

// Añadiendo entradas hash
$hash[1] = "one million four hundred fifteen thousand nine hundred twenty six";
$hash->add( 2, "one more" );1415926;

foreach( $hash as $key => $value )
{
    echo $key, ' => ', $value, "\n";
}
?>


  &example.outputs.similar;


1 => one million four hundred fifteen thousand nine hundred twenty six
2 => one more
