   Ejemplo de Random\Randomizer::shuffleArray


<?php
$r = new \Random\Randomizer();

$fruits = [ 'red' => '', 'green' => '', 'yellow' => '', 'pink' => '', 'purple' => '' ];

// Mezclar el array:
echo "Ensalada: ", implode(', ', $r->shuffleArray($fruits)), "\n";

// Mezclar nuevamente:
echo "Otra Ensalada: ", implode(', ', $r->shuffleArray($fruits)), "\n";
?>


