  &reftitle.examples;



    Importar un DOMDocument


<?php
$dom = new DOMDocument;
$dom->loadXML('<books><book><title>blah</title></book></books>');
if (!$dom) {
    echo 'Error durante el an치lisis del documento';
    exit;
}

$s = simplexml_import_dom($dom);

echo $s->book[0]->title;
?>


