  &reftitle.examples;



    Ejemplo básico de SwishSearch->setStructure


<?php

try {

    $swish = new Swish("index.swish-e");
    $search = $swish->prepare();

    $results = $search->execute("time");
    echo "Primera consulta: ", $results->hits, " coindencias encontradas\n";

    $search->setStructure(Swish::IN_TITLE|Swish::IN_HEAD); //buscar en title y head
    $results = $search->execute("time");
    echo "Segunda consulta: ", $results->hits, " coincidencias encontradas\n";

    $search->setStructure(Swish::IN_ALL); //buscar en el documento completo, el valor por defecto
    $results = $search->execute("time");
    echo "Tercera consulta: ", $results->hits, " coincidencias encontradas\n";

} catch (SwishException $e) {
    echo $e->getMessage(), "\n";
}

?>


    &example.outputs.similar;


First query found: 5 hits
Second query found: 0 hits
Third query found: 5 hits
