&reftitle.returnvalues;


 Devuelve &true; si var es un valor tipo
 float, &false; de lo contrario.

     &reftitle.examples;



    Ejemplo de is_float


<?php
if(is_float(27.25)) {
 echo "es flotante\n";
}else {
 echo "no es flotante\n";
}
var_dump(is_float('abc'));
var_dump(is_float(23));
var_dump(is_float(23.5));
var_dump(is_float(1e7));  //Notación Científica
var_dump(is_float(true));
?>


